var fundo = document.getElementsByClassName("fundo")[0];
var modal = document.getElementsByClassName("modal")[0];
var fundo_ = document.getElementsByClassName("fundo_")[0];
var modal_ = document.getElementsByClassName("modal_")[0];
var ifd=document.getElementById("btnif");



function abrirModal() {
	fundo.style.display = "flex";
	modal.style.display = "block";

}

function closeModal() {
	fundo.style.display = "none";
	modal.style.display = "none";

}

function abrirModal_() {
	fundo_.style.display = "flex";
	modal_.style.display = "block";

}

function closeModal_() {
	fundo_.style.display = "none";
	modal_.style.display = "none";

}
function closes() {
	fundo.style.display = "none";
	


}



